-- Create "org_invites" table
CREATE TABLE "org_invites" ("id" uuid NOT NULL, "receiver_email" character varying NOT NULL, "status" character varying NOT NULL DEFAULT 'pending', "created_at" timestamptz NOT NULL DEFAULT CURRENT_TIMESTAMP, "deleted_at" timestamptz NULL, "organization_id" uuid NOT NULL, "sender_id" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "org_invites_organizations_organization" FOREIGN KEY ("organization_id") REFERENCES "organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "org_invites_users_sender" FOREIGN KEY ("sender_id") REFERENCES "users" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
